/* =============================================================================
   MyBlog CSS - Custom styles without any framework
   ============================================================================= */

/* CSS Variables for theming */
:root {
    --color-bg: #ffffff;
    --color-bg-alt: #f8f9fa;
    --color-text: #212529;
    --color-text-muted: #6c757d;
    --color-primary: #0d6efd;
    --color-primary-hover: #0b5ed7;
    --color-border: #dee2e6;
    --color-danger: #dc3545;
    --color-success: #198754;
    --font-sans: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
    --font-mono: SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
    --max-width: 1200px;
    --spacing: 1rem;
    --radius: 0.375rem;
}

/* Reset and base styles */
*, *::before, *::after {
    box-sizing: border-box;
}

html {
    font-size: 16px;
    line-height: 1.5;
}

body {
    margin: 0;
    font-family: var(--font-sans);
    color: var(--color-text);
    background-color: var(--color-bg);
}

h1, h2, h3, h4, h5, h6 {
    margin-top: 0;
    margin-bottom: 0.5em;
    font-weight: 600;
    line-height: 1.2;
}

h1 { font-size: 2.5rem; }
h2 { font-size: 2rem; }
h3 { font-size: 1.5rem; }

p {
    margin-top: 0;
    margin-bottom: 1rem;
}

a {
    color: var(--color-primary);
    text-decoration: none;
}

a:hover {
    text-decoration: underline;
}

/* Container */
.container {
    max-width: var(--max-width);
    margin: 0 auto;
    padding: 0 var(--spacing);
}

/* Layout */
.layout {
    min-height: 100vh;
    display: flex;
    flex-direction: column;
}

/* Header */
.header {
    background-color: var(--color-bg-alt);
    border-bottom: 1px solid var(--color-border);
    padding: var(--spacing) 0;
}

.header .container {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: var(--spacing);
}

.logo {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--color-text);
}

.logo:hover {
    text-decoration: none;
}

.nav {
    display: flex;
    gap: var(--spacing);
    align-items: center;
}

.nav a {
    color: var(--color-text);
}

.logout-form {
    display: inline;
}

.logout-form button {
    background: none;
    border: none;
    color: var(--color-primary);
    cursor: pointer;
    font: inherit;
    padding: 0;
}

.logout-form button:hover {
    text-decoration: underline;
}

/* Main content */
.main {
    flex: 1;
    padding: calc(var(--spacing) * 2) 0;
}

/* Footer */
.footer {
    background-color: var(--color-bg-alt);
    border-top: 1px solid var(--color-border);
    padding: var(--spacing) 0;
    text-align: center;
    color: var(--color-text-muted);
}

/* Buttons */
.btn {
    display: inline-block;
    padding: 0.5rem 1rem;
    background-color: var(--color-bg-alt);
    border: 1px solid var(--color-border);
    border-radius: var(--radius);
    color: var(--color-text);
    cursor: pointer;
    font: inherit;
    text-decoration: none;
    transition: background-color 0.2s;
}

.btn:hover {
    background-color: var(--color-border);
    text-decoration: none;
}

.btn-primary {
    background-color: var(--color-primary);
    border-color: var(--color-primary);
    color: white;
}

.btn-primary:hover {
    background-color: var(--color-primary-hover);
}

.btn-link {
    background: none;
    border: none;
    color: var(--color-primary);
    cursor: pointer;
    padding: 0;
    font: inherit;
}

.btn-link:hover {
    text-decoration: underline;
}

.btn-link.danger {
    color: var(--color-danger);
}

/* Forms */
.form-group {
    margin-bottom: var(--spacing);
}

.form-group label {
    display: block;
    margin-bottom: 0.25rem;
    font-weight: 500;
}

.form-group input[type="text"],
.form-group input[type="password"],
.form-group input[type="email"],
.form-group textarea {
    width: 100%;
    padding: 0.5rem;
    border: 1px solid var(--color-border);
    border-radius: var(--radius);
    font: inherit;
}

.form-group textarea {
    resize: vertical;
}

.form-group.checkbox label {
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.form-group.checkbox input {
    width: auto;
}

.form-actions {
    display: flex;
    gap: 0.5rem;
    margin-top: var(--spacing);
}

/* Post cards */
.post-list {
    display: flex;
    flex-direction: column;
    gap: calc(var(--spacing) * 2);
}

.post-card {
    padding: var(--spacing);
    border: 1px solid var(--color-border);
    border-radius: var(--radius);
}

.post-card-title {
    margin-bottom: 0.5rem;
}

.post-card-title a {
    color: var(--color-text);
}

.post-card-meta {
    color: var(--color-text-muted);
    font-size: 0.875rem;
    margin-bottom: 0.5rem;
}

.post-card-meta span {
    margin-right: 1rem;
}

.draft-badge {
    background-color: var(--color-text-muted);
    color: white;
    padding: 0.125rem 0.5rem;
    border-radius: var(--radius);
    font-size: 0.75rem;
}

.read-more {
    font-weight: 500;
}

/* Pagination */
.pagination {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: var(--spacing);
    margin-top: calc(var(--spacing) * 2);
}

.page-info {
    color: var(--color-text-muted);
}

/* Post detail */
.post-detail {
    max-width: 800px;
}

.post-header {
    margin-bottom: calc(var(--spacing) * 2);
}

.post-meta {
    color: var(--color-text-muted);
}

.post-meta span {
    margin-right: 1rem;
}

.back-link {
    display: inline-block;
    margin-top: calc(var(--spacing) * 2);
}

/* Markdown content */
.markdown-content {
    line-height: 1.7;
}

.markdown-content h1,
.markdown-content h2,
.markdown-content h3,
.markdown-content h4,
.markdown-content h5,
.markdown-content h6 {
    margin-top: 1.5em;
}

.markdown-content pre {
    background-color: var(--color-bg-alt);
    padding: var(--spacing);
    border-radius: var(--radius);
    overflow-x: auto;
}

.markdown-content code {
    font-family: var(--font-mono);
    font-size: 0.875em;
}

.markdown-content :not(pre) > code {
    background-color: var(--color-bg-alt);
    padding: 0.125rem 0.25rem;
    border-radius: 0.25rem;
}

.markdown-content blockquote {
    border-left: 4px solid var(--color-border);
    margin: 0;
    padding-left: var(--spacing);
    color: var(--color-text-muted);
}

.markdown-content img {
    max-width: 100%;
    height: auto;
}

.markdown-content hr {
    border: none;
    border-top: 1px solid var(--color-border);
    margin: calc(var(--spacing) * 2) 0;
}

/* Login page */
.login-page {
    max-width: 400px;
    margin: 0 auto;
}

.error-message {
    background-color: #f8d7da;
    border: 1px solid #f5c6cb;
    color: #842029;
    padding: var(--spacing);
    border-radius: var(--radius);
    margin-bottom: var(--spacing);
}

/* Admin styles */
.dashboard-stats {
    display: flex;
    gap: var(--spacing);
    margin-bottom: calc(var(--spacing) * 2);
}

.stat-card {
    padding: var(--spacing);
    background-color: var(--color-bg-alt);
    border-radius: var(--radius);
    text-align: center;
}

.stat-value {
    font-size: 2rem;
    font-weight: 700;
    margin: 0;
}

.admin-nav {
    display: flex;
    gap: 0.5rem;
    margin-bottom: calc(var(--spacing) * 2);
}

.admin-table {
    width: 100%;
    border-collapse: collapse;
}

.admin-table th,
.admin-table td {
    padding: 0.75rem;
    text-align: left;
    border-bottom: 1px solid var(--color-border);
}

.admin-table th {
    background-color: var(--color-bg-alt);
    font-weight: 600;
}

/* Post editor */
.post-editor {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: calc(var(--spacing) * 2);
}

.editor-preview {
    border: 1px solid var(--color-border);
    border-radius: var(--radius);
    padding: var(--spacing);
    max-height: 80vh;
    overflow-y: auto;
}

.editor-preview h3 {
    margin-bottom: var(--spacing);
    padding-bottom: 0.5rem;
    border-bottom: 1px solid var(--color-border);
}

/* Image manager */
.image-upload {
    margin-bottom: calc(var(--spacing) * 2);
    padding: var(--spacing);
    background-color: var(--color-bg-alt);
    border-radius: var(--radius);
}

.image-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
    gap: var(--spacing);
}

.image-card {
    border: 1px solid var(--color-border);
    border-radius: var(--radius);
    overflow: hidden;
}

.image-card img {
    width: 100%;
    height: 150px;
    object-fit: cover;
}

.image-info {
    padding: 0.5rem;
}

.image-info p {
    margin: 0 0 0.25rem;
    font-size: 0.875rem;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.image-info code {
    display: block;
    font-size: 0.75rem;
    color: var(--color-text-muted);
    margin-bottom: 0.25rem;
}

.success {
    color: var(--color-success);
}

.error {
    color: var(--color-danger);
}

/* Responsive */
@media (max-width: 768px) {
    h1 { font-size: 2rem; }
    h2 { font-size: 1.5rem; }
    
    .header .container {
        flex-direction: column;
        text-align: center;
    }
    
    .nav {
        flex-wrap: wrap;
        justify-content: center;
    }
    
    .post-editor {
        grid-template-columns: 1fr;
    }
    
    .editor-preview {
        order: -1;
        max-height: 300px;
    }
    
    .dashboard-stats {
        flex-direction: column;
    }
    
    .admin-nav {
        flex-wrap: wrap;
    }
}
